    public static function render($iconName, $size = 24, $class = '') {
        if (!isset(self::$icons[$iconName])) {
            return '';
        }
        
        $icon = self::$icons[$iconName];
        $icon = str_replace('width="24"', 'width="' . $size . '"', $icon);
        $icon = str_replace('height="24"', 'height="' . $size . '"', $icon);
        $icon = str_replace('width="20"', 'width="' . $size . '"', $icon);
        $icon = str_replace('height="20"', 'height="' . $size . '"', $icon);
        
        if ($class) {
            $icon = str_replace('<svg', '<svg class="' . htmlspecialchars($class) . '"', $icon);
        }
        
        return $icon;
    }
